<?php

namespace InepZend\Module\Sqi\Service;

use InepZend\Service\AbstractService;

class VwQuestaoItemDependencia extends AbstractService
{

    public function getQuestaoItemDependencia($intIdQuestaoItem, $intCoProjeto, $intCoEvento)
    {
        return $this->getRepositoryEntity()->getQuestaoItemDependencia($intIdQuestaoItem, $intCoProjeto, $intCoEvento);
    }

    public function getQuestaoItemConfiguracao($intIdQuestaoItem, $intCoProjeto, $intCoEvento)
    {
        return $this->getRepositoryEntity()->getQuestaoItemConfiguracao($intIdQuestaoItem, $intCoProjeto, $intCoEvento);
    }

    public function findAll()
    {
        return $this->getRepositoryEntity()->findAll();
    }

}
